
public class BDDCalculator {
	int a;
	int b;
	public int getA() {
		return a;
	}
	public void setA(int a) {
		this.a = a;
	}
	public int getB() {
		return b;
	}
	public void setB(int b) {
		this.b = b;
	}
	
	public int add(){
		return this.a+ this.b;
	}
	public int subtract() {
		// TODO Auto-generated method stub
		return this.a-this.b;
	}
}
